import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'
import stockXLogo from '../images/stockx.png'
import goatLogo from '../images/goat.png'
import flightClubLogo from '../images/flightclub.png'
import stadiumGoodsLogo from '../images/stadiumgoods.png'
import sneaksLogo from '../images/Sneaks_Logo.png'
import ImgCarousel from './ImgCarousel';
import Spinner from 'react-bootstrap/Spinner'

const ProductCard = (props) => {
    const [loading, setLoading] = useState(true);
    const [sneaker, setSneaker] = useState({});
    const myHeaders = new Headers({
        "Content-Type": "application/json",
        Accept: "application/json"
      });

    useEffect(() => {

        const fetchSneaker = () => {
            fetch("http://localhost:8080/id/" + props.sneaker.styleID + '/prices', {
                    headers: myHeaders,
                })
                .then(response => response.json())
                .then(jsonResponse => {
                    setSneaker(jsonResponse);
                    setLoading(false);
                    console.log("this rndfsa")


                });
        }
        fetchSneaker();


    }, [props.show]);




    return(

        <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
<div class="procard">
  <nav>
    <svg class="arrow" version="1.1" viewBox="0 0 512 512" width="512px"  onClick={props.onHide}xmlns="http://www.w3.org/2000/svg" >
        <polygon points="352,115.4 331.3,96 160,256 331.3,416 352,396.7 201.5,256 " stroke="#727272"/></svg>
        <div style={{cursor:"pointer"}} onClick={props.onHide}> BACK TO ALL SHOES </div>
    <svg class="heart" version="1.1" viewBox="0 0 512 512" width="512px"  stroke="#727272" xmlns="http://www.w3.org/2000/svg" >
        <path d="M340.8,98.4c50.7,0,91.9,41.3,91.9,92.3c0,26.2-10.9,49.8-28.3,66.6L256,407.1L105,254.6c-15.8-16.6-25.6-39.1-25.6-63.9  c0-51,41.1-92.3,91.9-92.3c38.2,0,70.9,23.4,84.8,56.8C269.8,121.9,302.6,98.4,340.8,98.4 M340.8,83C307,83,276,98.8,256,124.8  c-20-26-51-41.8-84.8-41.8C112.1,83,64,131.3,64,190.7c0,27.9,10.6,54.4,29.9,74.6L245.1,418l10.9,11l10.9-11l148.3-149.8  c21-20.3,32.8-47.9,32.8-77.5C448,131.3,399.9,83,340.8,83L340.8,83z" stroke="#727272"/>
    </svg>
  </nav>
  <div class="photo">
      {loading ? <Spinner></Spinner>:<ImgCarousel sneaker ={sneaker} image={props.image}></ImgCarousel>}
  </div>
  <div class="description">
    <h2>{props.sneaker.shoeName}</h2>
    <h4></h4>
    <h1>${props.minPrice}</h1>
    <td dangerouslySetInnerHTML={{__html: props.sneaker.description_html}} />
  </div>
</div>
      </Modal>

    )
}

export default ProductCard;
