import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table'
import stockXLogo from '../images/stockx.png'
import goatLogo from '../images/goat.png'
import flightClubLogo from '../images/flightclub.png'
import stadiumGoodsLogo from '../images/stadiumgoods.png'

const PriceTable = (props) =>{
    const sizes = new Set([ 7, 7.5, 8, 8.5, 9, 9.5, 10]);
    var shoeSizes;
    var resellPrices = props.sneaker.resellPrices;
    var resellLinks = props.sneaker.resellLinks;

    for (var size in resellPrices.stockX) {
        sizes.add(size);
    }
    for (var size in resellPrices.flightClub) {
        sizes.add(size);
    }
    for (var size in resellPrices.goat) {
        sizes.add(size);
    }
    for (var size in resellPrices.stadiumGoods) {
        sizes.add(size);
    }
    shoeSizes = Array.from(sizes).sort(function (a, b) {
        return a - b;
    });




    return(
<div class='table-card scroll-bar'>
    <Table  responsive  >
    <thead >
        <tr>
        <th>US Size</th>
        {Array.from(shoeSizes).map((size, index) => (
            <th key={index}> {size} </th>
        ))}
        </tr>
    </thead>
    <tbody>
        <tr>
        <td onClick={()=> {window.open(resellLinks.stockX&&resellLinks.stockX, '_blank')}}><img src={stockXLogo}></img></td>
        {Array.from(shoeSizes).map((size, index) => (
            <td key={index}> {resellPrices.stockX&&resellPrices.stockX[size] ? '$' + resellPrices.stockX[size] : '-'}</td>
        ))}
        </tr>
        <tr>
        <td><img src={flightClubLogo}></img></td>
        {Array.from(shoeSizes).map((size, index) => (
            <td key={index}>{resellPrices.flightClub&&resellPrices.flightClub[size] ?  '$' + resellPrices.flightClub[size]: '-'}</td>
        ))}
        </tr>
        <tr>
        <td class='goat-row'><img src={goatLogo}></img></td>
        {Array.from(shoeSizes).map((size, index) => (
            <td key={index}> {resellPrices.goat && resellPrices.goat[size] ? '$' + resellPrices.goat[size]: '-'}</td>
        ))}
        </tr>
        <tr>
        <td onClick={()=> {window.open(resellLinks.stadiumGoods&&resellLinks.stadiumGoods)}}><img src={stadiumGoodsLogo}></img></td>
        {Array.from(shoeSizes).map((size, index) => (
            <td key={index}> {resellPrices.stadiumGoods && resellPrices.stadiumGoods[size] ? '$' + resellPrices.stadiumGoods[size]: '-'}</td>
        ))}
        </tr>
    </tbody>
    </Table>
    </div>


    )

}




export default PriceTable
