import './App.css';
import NavBar from './components/NavBar';
import SearchBar from './components/SearchBar';
import React, { useState, useEffect } from 'react';
import BrandIcons from './components/BrandIcons'
import MiniCard from './components/MiniCard'
import Spinner from 'react-bootstrap/Spinner'
import 'bootstrap/dist/css/bootstrap.min.css';

const SNEAKS_API = "http://localhost:8080/home"



const App = () => {

  const [loading, setLoading] = useState(true);
  const [sneakers, setSneakers] = useState([]);
  const [errorMessage, setErrorMessage] = useState(null);
  const myHeaders = new Headers({
    "Content-Type": "application/json",
    Accept: "application/json"
  });

    useEffect(() => {
    fetch(SNEAKS_API, {
      headers: myHeaders,
    })
      .then(response => response.json())
      .then(jsonResponse => {
        var trending = jsonResponse.slice(0,10);
        console.log(trending);
        setSneakers(trending);
        setLoading(false);
      });
  }, []);

    const search = searchValue => {
    setLoading(true);
    setErrorMessage(null);

    fetch('http://localhost:8080/search/'+searchValue)
      .then(response => response.json())
      .then(jsonResponse => {
        if (jsonResponse) {
          setSneakers(jsonResponse);
          setLoading(false);

        } else {
          setErrorMessage(jsonResponse.Error);
          setLoading(false);
        }
      });
  	};


    return (
      <div>
        <div>
          <NavBar />
        </div>
        <div class='background'>
          <div class='search-title'>
              <div class= 'title'>
                The Sneaks App
              </div>
              <div class= 'subtitle'>
                Search Sneakers and Compare Prices
              </div>
          </div>
          <SearchBar search = {search}/>
          <BrandIcons/>
        </div>
        <div class = 'trending'>
          <h2 class = 'title'>Trending Now</h2>
          <div class = 'product-page'> 
            {loading && !errorMessage ? (
            <Spinner class = 'spinners' animation="border" variant="secondary" role="status"></Spinner>

            ) : errorMessage ? (
            <div className="errorMessage">{errorMessage}</div>
            ) : (
            sneakers.map((sneaker, index) => (
            <MiniCard key={`${index}-${sneaker.shoename}`} sneaker={sneaker} />
            ))
            )}

          </div>



        </div>


      </div>

    );

}


export default App;
