import MiniCard from './MiniCard'
import Spinner from 'react-bootstrap/Spinner'
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';

const SNEAKS_API = "http://localhost:8080/home"

const Trending = () =>{

  const [loading, setLoading] = useState(true);
  const [seeAll, setSeeAll] = useState(false);
  const [sneakers, setSneakers] = useState([]);
  const [errorMessage, setErrorMessage] = useState(null);
  const myHeaders = new Headers({
    "Content-Type": "application/json",
    Accept: "application/json"
  });

    useEffect(() => {
    fetch(SNEAKS_API, {
      headers: myHeaders,
    })
      .then(response => response.json())
      .then(jsonResponse => {
        var trending = seeAll ? jsonResponse : jsonResponse.slice(0,10);
        setSneakers(trending);
        setLoading(false);
      });
  }, []);

  return(
    <div class = 'product-section'>
    <h2 class = 'title'>Trending Now {!seeAll ? <a href=""  class="see-all" ><div class="see-all-text">  See All </div> </a>: null}</h2>

    <div class = 'product-page'> 
      {loading && !errorMessage ? (
      <Spinner class = 'spinners' animation="border" variant="secondary" role="status"></Spinner>

      ) : errorMessage ? (
      <div className="errorMessage">{errorMessage}</div>
      ) : (
      sneakers.map((sneaker, index) => ( 

      <MiniCard key={`${index}-${sneaker.shoename}`} sneaker={sneaker} />
      ))
      )}

    </div>



  </div>
  )


}

export default Trending;
