import React, { useState, useEffect } from 'react';

import Card from 'react-bootstrap/Card'
import stockXLogo from '../images/stockx.png'
import goatLogo from '../images/goat.png'
import flightClubLogo from '../images/flightclub.png'
import stadiumGoodsLogo from '../images/stadiumgoods.png'
import sneaksLogo from '../images/Sneaks_Logo.png'
import ProductCard from './ProductCard'
var _ = require('lodash');



const MiniCard = (sneaker) => {
  const [showProductCard, setShowProductCard] = useState(false);

  const toggleProductCard = () =>{
    setShowProductCard(!showProductCard);
    console.log(showProductCard)
  }




  sneaker = sneaker.sneaker;
  var minPrice;

  var logo = _.minBy(_.keys(sneaker.lowestResellPrice), function (o) {
    return sneaker.lowestResellPrice[o];
  });

  if (logo == 'stockX') {
    logo = stockXLogo;
    minPrice = sneaker.lowestResellPrice.stockX;

  } else if (logo == 'stadiumGoods') {
    logo = stadiumGoodsLogo;
    minPrice = sneaker.lowestResellPrice.stadiumGoods;
  } else if (logo == 'goat') {
    logo = goatLogo;
    minPrice = sneaker.lowestResellPrice.goat;
  } else if (logo == 'flightClub') {
    logo = flightClubLogo;
    minPrice = sneaker.lowestResellPrice.flightClub;
  }
  var imageClass = !sneaker.imageLinks[0] ? 'default-image' : 'card-image';
  var sneakerImage = !sneaker.imageLinks[0] ? sneaksLogo : sneaker.imageLinks[0];


  const CardText = () => {
    if (minPrice) {
      return (
         <Card.Text class='mini-card-text'> 
        <div>From</div>
        <div class='mini-card-price'>${minPrice} <span class ='on-text'> on</span><img class= 'mini-logo' src={logo}></img></div>
      </Card.Text>
    );
  }
  else{
    return(
      <Card.Text class='mini-card-text'> 
        <div>Not Available</div>
      </Card.Text>

      );
    }

  }


    return(
      <a onClick={() => toggleProductCard} style={{ cursor: 'pointer' }} class='card-button'>
<Card border="light" tag="a"  style={{ cursor: "pointer" }} style={{ width: '15rem', height: '17rem' }}>
  <Card.Img class= {imageClass} variant="top" src={sneakerImage} />
  <Card.Body class = 'mini-card-body'>
    <Card.Title class= 'card-title'>{sneaker.shoeName}</Card.Title>
    <CardText/>
  </Card.Body>
</Card>
<ProductCard show={showProductCard} onHide={() => toggleProductCard}></ProductCard>
</a>


    );

}
export default MiniCard;
