import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const SearchBar = (props) => {
    let history = useHistory();
    const [searchValue, setSearchValue] = useState("");

    const handleSearchInputChanges = (e) => {
      setSearchValue(e.target.value);
    }

    const callSearchFunction = (e) => {
      e.preventDefault();
      history.push(process.env.PUBLIC_URL+'/search/'+searchValue);
    }

    return (
        <div class="search-bar">
            <form class= "test" onSubmit={callSearchFunction}>
              <div class="inner-form">
                <div class="basic-search">
                  <div class="input-field">
                    <input  value={searchValue} onChange={handleSearchInputChanges} type="text" placeholder = "Search Shoe"/>
                    <div class="icon-wrap">
                      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" onClick={callSearchFunction} type="submit" value="SEARCH" >
                        <path
                          d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z">
                        </path>
                      </svg>
                    </div>
                  </div>
                </div>
              </div>
            </form>
    </div>
      );
}
export default SearchBar
