import MiniCard from './MiniCard'
import Spinner from 'react-bootstrap/Spinner'
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';





const Products = ({ match, location }) => {
    const myHeaders = new Headers({
        "Content-Type": "application/json",
        Accept: "application/json"
      });
    const [loading, setLoading] = useState(true);
    const [sneakers, setSneakers] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);
    const { params: { key } } = match;
    const SNEAKS_API = "http://localhost:8080/search/"+key;

    useEffect(() => {
        setLoading(true);
        window.scrollTo({
          top:200,
          behavior: 'smooth'
        })
        fetch(SNEAKS_API, {
            headers: myHeaders,
          })
          .then(response => response.json())
          .then(jsonResponse => {
              console.log("THIS RAN")
            setSneakers(jsonResponse);
            setLoading(false);
            window.scrollTo({
              top:600,
              behavior: 'smooth'
            })
          });
      }, [location]);

    return(
        <div class = 'trending'>
        <h2 class = 'product-title'> Results for <span class="product-key">'{key}'</span> </h2>

        <div class = 'product-page'> 
        {loading && !errorMessage ? (
      <Spinner class = 'spinners' animation="border" variant="secondary" role="status"></Spinner>

      ) : errorMessage ? (
      <div className="errorMessage">{errorMessage}</div>
      ) : (
      sneakers.map((sneaker, index) => ( 

      <MiniCard key={`${index}-${sneaker.shoename}`} sneaker={sneaker} />
      ))
      )}


        </div>



      </div>
      )

}

export default Products;
